import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import './Posts.css';
import { parse } from 'marked';

// import axios from "axios";

console.log("in posts");

export default function Posts() {
    console.log("in in posts");

    const [blogs, setBlogs] = useState([{
        title: '',
        content: ''
    }])

    useEffect(() => {
        fetch("/getposts").then(res => {
            if (res.ok) {
                return res.json();
            }
        }).then(jsonRes => setBlogs(jsonRes));
    })

    // const postSlug = slugify(postTitle, {
    //     lower: true, // Convert to lowercase
    //     strict: true, // Replace special characters with dashes
    // });

    const renderText = (content) => {
        const html = parse(content, { sanitize: true });
        return { __html: html };
    };

    return (
        <div>
            <h1 id='header'>Posts</h1>
            {blogs.map(blog =>
                <div className='card'>
                    {/* <div className="container-sm"> */}
                        <h3>
                            <Link to={`/posts/${blog.slug}`} className="nowrap-title">
                                {blog.title}
                            </Link>
                        </h3>
                        <div className="snippet">
                            {/* <p dangerouslySetInnerHTML={{ __html: blog.body }}></p> */}
                            {dangerouslySetInnerHTML=renderText(blog.content)}
                            <p>{dangerouslySetInnerHTML.length > 100 ? dangerouslySetInnerHTML.substring(0, 100) + "..." : dangerouslySetInnerHTML.content}</p>
                            <Link to={`/posts/${blog.slug}`} className="nowrap-body">
                                {dangerouslySetInnerHTML.length > 100 ? "Read more" : "Go to post"}{" "}
                            {/* <i className="fas fa-arrow-circle-right"></i> */}
                            </Link>
                        </div>
                    {/* </div> */}
                </div>
            )}
        </div>
        // <div className='container'>
        //     <h1>Posts</h1>
        //         {blogs.map(blog => 
        //             <div>
        //                 <h1>{blog.title}</h1>
        //                 <p>{blog.content}</p>
        //                 <Link to={`/posts/${blog.slug}`}>Read More</Link> {/* Use the Link component to link to the individual blog post page */}
        //             </div>
        //         )}
        // </div>
    )
}

